import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const API_URL = 'http://localhost:5000/students';

function App() {
  const [formData, setFormData] = useState({
    studentId: '', fullName: '', dob: '', gender: '',
    email: '', phone: '', program: '', major: ''
  });
  const [students, setStudents] = useState([]);
  const [isEditing, setIsEditing] = useState(false);

  // Fetch all students
  const fetchStudents = async () => {
    try {
      const response = await axios.get(API_URL);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle form changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add Student
  const handleAddSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      toast.success('Student added successfully!');
      fetchStudents();
      setFormData({ studentId: '', fullName: '', dob: '', gender: '', email: '', phone: '', program: '', major: '' });
    } catch (error) {
      toast.error('Error adding student!');
    }
  };

  // Update Student
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/${formData.studentId}`, formData);
      toast.success('Student updated successfully!');
      fetchStudents();
      setFormData({ studentId: '', fullName: '', dob: '', gender: '', email: '', phone: '', program: '', major: '' });
      setIsEditing(false);
    } catch (error) {
      toast.error('Error updating student!');
    }
  };

  // Delete Student
  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      toast.success('Student deleted!');
      fetchStudents();
    } catch (error) {
      toast.error('Error deleting student!');
    }
  };

  // Populate form for editing
  const handleEdit = (student) => {
    setFormData(student);
    setIsEditing(true);
  };

  return (
    <div className="container" style={{ textAlign: 'center' }}>
      <h1>Student Management</h1>

      {!isEditing ? (
        <form onSubmit={handleAddSubmit}>
          <input type="text" name="studentId" placeholder="Student ID" value={formData.studentId} onChange={handleChange} required />
          <input type="text" name="fullName" placeholder="Full Name" value={formData.fullName} onChange={handleChange} required />
          <input type="date" name="dob" value={formData.dob} onChange={handleChange} required />
          <select name="gender" value={formData.gender} onChange={handleChange} required>
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
          <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
          <input type="tel" name="phone" placeholder="Phone" value={formData.phone} onChange={handleChange} required />
          <input type="text" name="program" placeholder="Program" value={formData.program} onChange={handleChange} required />
          <input type="text" name="major" placeholder="Major" value={formData.major} onChange={handleChange} required />
          <button type="submit">Add Student</button>
        </form>
      ) : (
        <form onSubmit={handleEditSubmit}>
          <input type="text" name="studentId" value={formData.studentId} disabled />
          <input type="text" name="fullName" value={formData.fullName} onChange={handleChange} required />
          <button type="submit">Update Student</button>
        </form>
      )}

      <h2>Student List</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Program</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((s) => (
            <tr key={s.studentId}>
              <td>{s.studentId}</td>
              <td>{s.fullName}</td>
              <td>{s.program}</td>
              <td>
                <button onClick={() => handleEdit(s)}>Edit</button>
                <button onClick={() => handleDelete(s.studentId)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <ToastContainer />
    </div>
  );
}

export default App;
