import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const API_URL = 'http://localhost:6379/students';

function App() {
  const [formData, setFormData] = useState({
    studentId: '',
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    email: '',
    phone: '',
    emergencyName: '',
    emergencyRelation: '',
    emergencyPhone: '',
    program: '',
    major: ''
  });
  const [students, setStudents] = useState([]);
  const [isEditing, setIsEditing] = useState(false);

  const fetchStudents = async () => {
    try {
      const response = await axios.get(API_URL);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      toast.success('Student added successfully!');
      fetchStudents();
      setFormData({
        studentId: '', firstName: '', middleName: '', lastName: '', dateOfBirth: '',
        gender: '', email: '', phone: '', emergencyName: '', emergencyRelation: '', emergencyPhone: '',
        program: '', major: ''
      });
    } catch (error) {
      toast.error('Error adding student!');
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/${formData.studentId}`, formData);
      toast.success('Student updated successfully!');
      fetchStudents();
      setFormData({
        studentId: '', firstName: '', middleName: '', lastName: '', dateOfBirth: '',
        gender: '', email: '', phone: '', emergencyName: '', emergencyRelation: '', emergencyPhone: '',
        program: '', major: ''
      });
      setIsEditing(false);
    } catch (error) {
      toast.error('Error updating student!');
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      toast.success('Student deleted!');
      fetchStudents();
    } catch (error) {
      toast.error('Error deleting student!');
    }
  };

  const handleEdit = (student) => {
    setFormData(student);
    setIsEditing(true);
  };

  return (
    <div className="container" style={{ textAlign: 'center' }}>
      <h1>Student CRUD with Redis</h1>
      <form onSubmit={isEditing ? handleEditSubmit : handleAddSubmit}>
        <input type="text" name="studentId" placeholder="Student ID" value={formData.studentId} onChange={handleChange} required />
        <input type="text" name="firstName" placeholder="First Name" value={formData.firstName} onChange={handleChange} required />
        <input type="text" name="middleName" placeholder="Middle Name" value={formData.middleName} onChange={handleChange} />
        <input type="text" name="lastName" placeholder="Last Name" value={formData.lastName} onChange={handleChange} required />
        <input type="date" name="dateOfBirth" value={formData.dateOfBirth} onChange={handleChange} required />
        <select name="gender" value={formData.gender} onChange={handleChange} required>
          <option value="">Select Gender</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Other">Other</option>
        </select>
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
        <input type="tel" name="phone" placeholder="Phone Number" value={formData.phone} onChange={handleChange} required />
        <input type="text" name="emergencyName" placeholder="Emergency Contact Name" value={formData.emergencyName} onChange={handleChange} required />
        <input type="text" name="emergencyRelation" placeholder="Emergency Contact Relationship" value={formData.emergencyRelation} onChange={handleChange} required />
        <input type="tel" name="emergencyPhone" placeholder="Emergency Contact Phone" value={formData.emergencyPhone} onChange={handleChange} required />
        <input type="text" name="program" placeholder="Program" value={formData.program} onChange={handleChange} required />
        <input type="text" name="major" placeholder="Major" value={formData.major} onChange={handleChange} required />
        <button type="submit">{isEditing ? 'Update Student' : 'Add Student'}</button>
      </form>

      <h2>Student List</h2>
      <table border="1" align="center" style={{ width: '80%' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Full Name</th>
            <th>DOB</th>
            <th>Gender</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Program</th>
            <th>Major</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.studentId}>
              <td>{student.studentId}</td>
              <td>{`${student.firstName} ${student.middleName} ${student.lastName}`}</td>
              <td>{student.dateOfBirth}</td>
              <td>{student.gender}</td>
              <td>{student.email}</td>
              <td>{student.phone}</td>
              <td>{student.program}</td>
              <td>{student.major}</td>
              <td>
                <button onClick={() => handleEdit(student)}>Edit</button>
                <button onClick={() => handleDelete(student.studentId)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <ToastContainer />
    </div>
  );
}

export default App;
