import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const API_URL = 'http://localhost:5000/students';

// Inline styles for background and overlay
const backgroundStyle = {
  backgroundImage: 'url("https://source.unsplash.com/1600x900/?education")',
  backgroundSize: 'cover',
  backgroundRepeat: 'no-repeat',
  backgroundPosition: 'center',
  minHeight: '100vh'
};

const overlayStyle = {
  backgroundColor: 'rgba(0, 0, 0, 0.6)',
  minHeight: '100vh',
  paddingTop: '20px',
  paddingBottom: '20px'
};

function App() {
  const [formData, setFormData] = useState({
    studentId: '', fullName: '', dob: '', gender: '',
    email: '', phone: '', program: '', major: ''
  });
  const [students, setStudents] = useState([]);
  const [isEditing, setIsEditing] = useState(false);

  // Fetch all students from the backend
  const fetchStudents = async () => {
    try {
      const response = await axios.get(API_URL);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add Student (POST)
  const handleAddSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      toast.success('Student added successfully!');
      fetchStudents();
      setFormData({
        studentId: '', fullName: '', dob: '', gender: '',
        email: '', phone: '', program: '', major: ''
      });
    } catch (error) {
      toast.error('Error adding student!');
    }
  };

  // Update Student (PUT)
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/${formData.studentId}`, formData);
      toast.success('Student updated successfully!');
      fetchStudents();
      setFormData({
        studentId: '', fullName: '', dob: '', gender: '',
        email: '', phone: '', program: '', major: ''
      });
      setIsEditing(false);
    } catch (error) {
      toast.error('Error updating student!');
    }
  };

  // Delete Student (DELETE)
  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      toast.success('Student deleted!');
      fetchStudents();
    } catch (error) {
      toast.error('Error deleting student!');
    }
  };

  // Populate form for editing
  const handleEdit = (student) => {
    setFormData(student);
    setIsEditing(true);
  };

  return (
    <div style={backgroundStyle}>
      <div style={overlayStyle}>
        <div className="container text-light">
          <h1 className="text-center mb-4">Student Management System</h1>
          <p className="text-center mb-5">Manage your students efficiently with a clean, user-friendly interface.</p>

          <div className="card mb-5 shadow">
            <div className="card-body">
              <h4 className="card-title mb-3">{isEditing ? 'Update Student' : 'Add New Student'}</h4>
              <form onSubmit={isEditing ? handleEditSubmit : handleAddSubmit}>
                <div className="row">
                  <div className="col-md-4 mb-3">
                    <label className="form-label">Student ID</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      name="studentId" 
                      placeholder="Enter Student ID" 
                      value={formData.studentId} 
                      onChange={handleChange} 
                      required 
                      disabled={isEditing}
                    />
                  </div>
                  <div className="col-md-8 mb-3">
                    <label className="form-label">Full Name</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      name="fullName" 
                      placeholder="Enter Full Name" 
                      value={formData.fullName} 
                      onChange={handleChange} 
                      required 
                    />
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-3 mb-3">
                    <label className="form-label">Date of Birth</label>
                    <input 
                      type="date" 
                      className="form-control" 
                      name="dob" 
                      value={formData.dob} 
                      onChange={handleChange} 
                      required 
                    />
                  </div>
                  <div className="col-md-3 mb-3">
                    <label className="form-label">Gender</label>
                    <select 
                      className="form-select" 
                      name="gender" 
                      value={formData.gender} 
                      onChange={handleChange} 
                      required
                    >
                      <option value="">Select Gender</option>
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Other">Other</option>
                    </select>
                  </div>
                  <div className="col-md-3 mb-3">
                    <label className="form-label">Email</label>
                    <input 
                      type="email" 
                      className="form-control" 
                      name="email" 
                      placeholder="Enter Email" 
                      value={formData.email} 
                      onChange={handleChange} 
                      required 
                    />
                  </div>
                  <div className="col-md-3 mb-3">
                    <label className="form-label">Phone</label>
                    <input 
                      type="tel" 
                      className="form-control" 
                      name="phone" 
                      placeholder="Enter Phone Number" 
                      value={formData.phone} 
                      onChange={handleChange} 
                      required 
                    />
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label className="form-label">Program</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      name="program" 
                      placeholder="Enter Program" 
                      value={formData.program} 
                      onChange={handleChange} 
                      required 
                    />
                  </div>
                  <div className="col-md-6 mb-3">
                    <label className="form-label">Major</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      name="major" 
                      placeholder="Enter Major" 
                      value={formData.major} 
                      onChange={handleChange} 
                      required 
                    />
                  </div>
                </div>
                <button type="submit" className="btn btn-primary">
                  {isEditing ? 'Update Student' : 'Add Student'}
                </button>
              </form>
            </div>
          </div>

          <h2 className="mb-4">Student List</h2>
          <div className="table-responsive shadow">
            <table className="table table-striped table-hover">
              <thead className="table-dark">
                <tr>
                  <th>Student ID</th>
                  <th>Full Name</th>
                  <th>DOB</th>
                  <th>Gender</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Program</th>
                  <th>Major</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {students.map((s) => (
                  <tr key={s.studentId}>
                    <td>{s.studentId}</td>
                    <td>{s.fullName}</td>
                    <td>{s.dob}</td>
                    <td>{s.gender}</td>
                    <td>{s.email}</td>
                    <td>{s.phone}</td>
                    <td>{s.program}</td>
                    <td>{s.major}</td>
                    <td>
                      <button className="btn btn-warning btn-sm me-2" onClick={() => handleEdit(s)}>Edit</button>
                      <button className="btn btn-danger btn-sm" onClick={() => handleDelete(s.studentId)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <ToastContainer />
        </div>
      </div>
    </div>
  );
}

export default App;
