import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const API_URL = 'http://localhost:5000/students';

function App() {
  const [formData, setFormData] = useState({
    studentId: '', fullName: '', dob: '', gender: '',
    email: '', phone: '', program: '', major: ''
  });
  const [students, setStudents] = useState([]);
  const [isEditing, setIsEditing] = useState(false);

  // Fetch all students
  const fetchStudents = async () => {
    try {
      const response = await axios.get(API_URL);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle form changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add Student
  const handleAddSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      toast.success('Student added successfully!');
      fetchStudents();
      setFormData({ studentId: '', fullName: '', dob: '', gender: '', email: '', phone: '', program: '', major: '' });
    } catch (error) {
      toast.error('Error adding student!');
    }
  };

  // Update Student
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/${formData.studentId}`, formData);
      toast.success('Student updated successfully!');
      fetchStudents();
      setFormData({ studentId: '', fullName: '', dob: '', gender: '', email: '', phone: '', program: '', major: '' });
      setIsEditing(false);
    } catch (error) {
      toast.error('Error updating student!');
    }
  };

  // Delete Student
  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      toast.success('Student deleted!');
      fetchStudents();
    } catch (error) {
      toast.error('Error deleting student!');
    }
  };

  // Populate form for editing
  const handleEdit = (student) => {
    setFormData(student);
    setIsEditing(true);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Student Management</h1>

      <div className="card mb-4">
        <div className="card-body">
          <form onSubmit={isEditing ? handleEditSubmit : handleAddSubmit}>
            <div className="mb-3">
              <label className="form-label">Student ID</label>
              <input 
                type="text" 
                className="form-control" 
                name="studentId" 
                placeholder="Enter Student ID" 
                value={formData.studentId} 
                onChange={handleChange} 
                required 
                disabled={isEditing}
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Full Name</label>
              <input 
                type="text" 
                className="form-control" 
                name="fullName" 
                placeholder="Enter Full Name" 
                value={formData.fullName} 
                onChange={handleChange} 
                required 
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Date of Birth</label>
              <input 
                type="date" 
                className="form-control" 
                name="dob" 
                value={formData.dob} 
                onChange={handleChange} 
                required 
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Gender</label>
              <select 
                className="form-select" 
                name="gender" 
                value={formData.gender} 
                onChange={handleChange} 
                required
              >
                <option value="">Select Gender</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </div>
            <div className="mb-3">
              <label className="form-label">Email</label>
              <input 
                type="email" 
                className="form-control" 
                name="email" 
                placeholder="Enter Email" 
                value={formData.email} 
                onChange={handleChange} 
                required 
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Phone</label>
              <input 
                type="tel" 
                className="form-control" 
                name="phone" 
                placeholder="Enter Phone Number" 
                value={formData.phone} 
                onChange={handleChange} 
                required 
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Program</label>
              <input 
                type="text" 
                className="form-control" 
                name="program" 
                placeholder="Enter Program" 
                value={formData.program} 
                onChange={handleChange} 
                required 
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Major</label>
              <input 
                type="text" 
                className="form-control" 
                name="major" 
                placeholder="Enter Major" 
                value={formData.major} 
                onChange={handleChange} 
                required 
              />
            </div>
            <button type="submit" className="btn btn-primary">
              {isEditing ? 'Update Student' : 'Add Student'}
            </button>
          </form>
        </div>
      </div>

      <h2 className="mb-3">Student List</h2>
      <div className="table-responsive">
        <table className="table table-striped">
          <thead className="table-dark">
            <tr>
              <th>Student ID</th>
              <th>Full Name</th>
              <th>Program</th>
              <th>Major</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student.studentId}>
                <td>{student.studentId}</td>
                <td>{student.fullName}</td>
                <td>{student.program}</td>
                <td>{student.major}</td>
                <td>
                  <button className="btn btn-warning me-2" onClick={() => handleEdit(student)}>Edit</button>
                  <button className="btn btn-danger" onClick={() => handleDelete(student.studentId)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <ToastContainer />
    </div>
  );
}

export default App;
