import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import 'react-toastify/dist/ReactToastify.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faSearch,
  faUpload,
  faEdit,
  faTrash,
  faSortUp,
  faSortDown,
  faMoon,
  faSun,
  faUserShield,
  faUser
} from '@fortawesome/free-solid-svg-icons';
import './App.css';

Chart.register(...registerables);
Chart.defaults.font.family = 'Roboto, sans-serif';
Chart.defaults.font.size = 14;

const API_URL = 'http://localhost:5000/students';

function App() {
  const [formData, setFormData] = useState({
    studentId: '',
    lastName: '',
    firstName: '',
    middleName: '',
    dob: '',
    gender: '',
    email: '',
    phone: '',
    program: '',
    major: ''
  });
  const [students, setStudents] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [csvFile, setCsvFile] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    const savedMode = localStorage.getItem('darkMode');
    if (savedMode !== null) {
      setIsDarkMode(savedMode === 'true');
    } else {
      const systemPrefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setIsDarkMode(systemPrefersDark);
    }
  }, []);

  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
    localStorage.setItem('darkMode', isDarkMode);
  }, [isDarkMode]);

  const computedStats = useMemo(() => {
    const stats = {};
    students.forEach((student) => {
      const prog = student.program?.trim() || 'Unknown';
      stats[prog] = (stats[prog] || 0) + 1;
    });
    return stats;
  }, [students]);

  const parseFullName = (fullName) => {
    if (!fullName) return { lastName: '', firstName: '', middleName: '' };
    if (fullName.includes(',')) {
      const parts = fullName.split(',');
      const lastName = parts[0].trim();
      const names = parts[1].trim().split(/\s+/);
      return {
        lastName,
        firstName: names[0] || '',
        middleName: names.slice(1).join(' ') || ''
      };
    }
    const parts = fullName.trim().split(/\s+/);
    return parts.length === 1
      ? { lastName: '', firstName: parts[0], middleName: '' }
      : {
          lastName: parts.pop() || '',
          firstName: parts.shift() || '',
          middleName: parts.join(' ') || ''
        };
  };

  const sortedStudents = useMemo(() => {
    let sortable = [...students];
    if (sortConfig.key) {
      sortable.sort((a, b) => {
        const aNames = parseFullName(a.fullName);
        const bNames = parseFullName(b.fullName);
        const aValue = ['lastName', 'firstName', 'middleName'].includes(sortConfig.key)
          ? aNames[sortConfig.key]?.toLowerCase() || ''
          : a[sortConfig.key]?.toString().toLowerCase() || '';
        const bValue = ['lastName', 'firstName', 'middleName'].includes(sortConfig.key)
          ? bNames[sortConfig.key]?.toLowerCase() || ''
          : b[sortConfig.key]?.toString().toLowerCase() || '';

        return aValue.localeCompare(bValue) * (sortConfig.direction === 'asc' ? 1 : -1);
      });
    }
    return sortable;
  }, [students, sortConfig]);

  const filteredStudents = useMemo(() => {
    const query = searchQuery.toLowerCase();
    return sortedStudents.filter(student =>
      Object.values(student).some(value =>
        value?.toString().toLowerCase().includes(query)
      )
    );
  }, [searchQuery, sortedStudents]);

  const fetchStudents = async () => {
    try {
      const { data } = await axios.get(API_URL);
      setStudents(data);
    } catch (error) {
      toast.error('Failed to fetch students');
    }
  };

  useEffect(() => { fetchStudents(); }, []);

  const requestSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const combineFullName = () => {
    const { lastName, firstName, middleName } = formData;
    return [lastName, [firstName, middleName].filter(Boolean).join(' ')]
      .filter(Boolean)
      .join(', ');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      ...formData,
      fullName: combineFullName(),
      studentId: formData.studentId
    };
    delete payload.lastName;
    delete payload.firstName;
    delete payload.middleName;

    try {
      const url = isEditing ? `${API_URL}/${formData.studentId}` : API_URL;
      const method = isEditing ? 'put' : 'post';
      await axios[method](url, payload, isEditing ? { headers: { 'x-role': 'admin' } } : {});
      toast.success(`Student ${isEditing ? 'updated' : 'added'} successfully!`);
      fetchStudents();
      setFormData({
        studentId: '', lastName: '', firstName: '', middleName: '',
        dob: '', gender: '', email: '', phone: '', program: '', major: ''
      });
      setIsEditing(false);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Operation failed');
    }
  };

  const handleEdit = (student) => {
    const names = parseFullName(student.fullName);
    setFormData({ ...student, ...names });
    setIsEditing(true);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`, { headers: { 'x-role': 'admin' } });
      toast.success('Student deleted');
      fetchStudents();
    } catch (error) {
      toast.error('Delete failed');
    }
  };

  const handleCSVUpload = async () => {
    if (!csvFile) return toast.error('Select CSV file first');
    const formData = new FormData();
    formData.append('file', csvFile);
    try {
      await axios.post(`${API_URL}/upload`, formData, {
        headers: { 'Content-Type': 'multipart/form-data', 'x-role': 'admin' }
      });
      toast.success('CSV uploaded');
      fetchStudents();
    } catch (error) {
      toast.error('CSV upload failed');
    }
  };

  const chartData = {
    labels: Object.keys(computedStats),
    datasets: [{
      data: Object.values(computedStats),
      backgroundColor: [
        'rgba(67, 97, 238, 0.7)',
        'rgba(63, 55, 201, 0.7)',
        'rgba(72, 149, 239, 0.7)',
        'rgba(76, 201, 240, 0.7)'
      ],
      borderColor: [
        'rgba(67, 97, 238, 1)',
        'rgba(63, 55, 201, 1)',
        'rgba(72, 149, 239, 1)',
        'rgba(76, 201, 240, 1)'
      ],
      borderWidth: 1,
      borderRadius: 8
    }]
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <div className="header-content">
          <h1 className="app-title">
            <span className="gradient-text">Student Management</span>
            <span className="version-badge">v2.0</span>
          </h1>
          <div className="header-controls">
            <button className="theme-toggle" onClick={() => setIsDarkMode(!isDarkMode)}>
              <FontAwesomeIcon icon={isDarkMode ? faSun : faMoon} />
            </button>
            <button 
              className={`admin-toggle ${isAdmin ? 'active' : ''}`}
              onClick={() => setIsAdmin(!isAdmin)}
            >
              <FontAwesomeIcon icon={isAdmin ? faUserShield : faUser} />
              <span>{isAdmin ? 'Admin Mode' : 'Guest Mode'}</span>
            </button>
          </div>
        </div>
      </header>

      <main className="app-main">
        <section className="search-section">
          <div className="search-container">
            <FontAwesomeIcon icon={faSearch} className="search-icon" />
            <input
              type="search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search students..."
              className="search-input"
            />
          </div>
        </section>

        <section className="form-section">
          <div className="form-card">
            <h2 className="form-title">
              {isEditing ? '✏️ Edit Student' : '➕ Add New Student'}
            </h2>
            <form onSubmit={handleSubmit}>
              <div className="form-grid">
                <div className="form-group">
                  <label>Student ID</label>
                  <input
                    type="text"
                    name="studentId"
                    value={formData.studentId}
                    onChange={handleChange}
                    disabled={isEditing}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Middle Name</label>
                  <input
                    type="text"
                    name="middleName"
                    value={formData.middleName}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label>Date of Birth</label>
                  <input
                    type="date"
                    name="dob"
                    value={formData.dob}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Gender</label>
                  <select
                    name="gender"
                    value={formData.gender}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Phone</label>
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Program</label>
                  <input
                    type="text"
                    name="program"
                    value={formData.program}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Major</label>
                  <input
                    type="text"
                    name="major"
                    value={formData.major}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <button type="submit" className="btn-primary">
                {isEditing ? 'Update Student' : 'Add Student'}
              </button>
            </form>
          </div>
        </section>

        {isAdmin && (
          <section className="admin-section">
            <div className="admin-card">
              <h3>📊 Program Distribution</h3>
              <div className="chart-container">
                <Bar 
                  data={chartData}
                  options={{
                    plugins: { legend: { display: false } },
                    scales: {
                      y: { beginAtZero: true, grid: { color: 'rgba(255,255,255,0.1)' } },
                      x: { grid: { color: 'rgba(255,255,255,0.1)' } }
                    }
                  }}
                />
              </div>
              <div className="csv-upload">
                <input
                  type="file"
                  accept=".csv"
                  onChange={(e) => setCsvFile(e.target.files[0])}
                />
                <button onClick={handleCSVUpload} className="btn-primary">
                  <FontAwesomeIcon icon={faUpload} /> Upload CSV
                </button>
              </div>
            </div>
          </section>
        )}

        <section className="data-section">
          <div className="table-card">
            <div className="table-header">
              <h3>🧑🎓 Student Records ({filteredStudents.length})</h3>
            </div>
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    {['studentId', 'lastName', 'firstName', 'dob', 'gender', 'email', 'program'].map((key) => (
                      <th key={key} onClick={() => requestSort(key)}>
                        {key.replace(/([A-Z])/g, ' $1').trim()}
                        {sortConfig.key === key && (
                          <FontAwesomeIcon 
                            icon={sortConfig.direction === 'asc' ? faSortUp : faSortDown} 
                          />
                        )}
                      </th>
                    ))}
                    {isAdmin && <th>Actions</th>}
                  </tr>
                </thead>
                <tbody>
                  {filteredStudents.map((student) => {
                    const names = parseFullName(student.fullName);
                    return (
                      <tr key={student.studentId}>
                        <td>{student.studentId}</td>
                        <td>{names.lastName}</td>
                        <td>{names.firstName}</td>
                        <td>{student.dob}</td>
                        <td>{student.gender}</td>
                        <td>{student.email}</td>
                        <td>{student.program}</td>
                        {isAdmin && (
                          <td className="actions">
                            <button 
                              className="btn-warning"
                              onClick={() => handleEdit(student)}
                            >
                              <FontAwesomeIcon icon={faEdit} />
                            </button>
                            <button
                              className="btn-danger"
                              onClick={() => handleDelete(student.studentId)}
                            >
                              <FontAwesomeIcon icon={faTrash} />
                            </button>
                          </td>
                        )}
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </section>
      </main>

      <ToastContainer
        position="bottom-right"
        theme={isDarkMode ? 'dark' : 'light'}
        autoClose={3000}
      />
    </div>
  );
}

export default App;
