import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

Chart.register(...registerables);

const API_URL = 'http://localhost:5000/students';

function App() {
  const [formData, setFormData] = useState({
    studentId: '', fullName: '', dob: '', gender: '',
    email: '', phone: '', program: '', major: ''
  });
  const [students, setStudents] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [csvFile, setCsvFile] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [stats, setStats] = useState({});
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

  // Helper function to reformat the full name for display
  const formatFullName = (fullName) => {
    if (!fullName) return '';
    const parts = fullName.trim().split(/\s+/);
    if (parts.length === 0) return '';
    if (parts.length === 1) return parts[0];
    const firstName = parts[0];
    const lastName = parts[parts.length - 1];
    const middleNames = parts.slice(1, parts.length - 1).join(' ');
    return middleNames ? `${lastName}, ${firstName} ${middleNames}` : `${lastName}, ${firstName}`;
  };

  // Fetch all students
  const fetchStudents = async () => {
    try {
      const response = await axios.get(API_URL);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  // Fetch stats for visualization
  const fetchStats = async () => {
    try {
      const response = await axios.get(`${API_URL}/stats`);
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  useEffect(() => {
    fetchStudents();
    if (isAdmin) fetchStats();
  }, [isAdmin]);

  // Sorting: compute sorted student list
  const sortedStudents = useMemo(() => {
    let sortableStudents = [...students];
    if (sortConfig.key !== null) {
      sortableStudents.sort((a, b) => {
        const aValue = a[sortConfig.key] ? a[sortConfig.key].toString().toLowerCase() : '';
        const bValue = b[sortConfig.key] ? b[sortConfig.key].toString().toLowerCase() : '';
        if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
        if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
        return 0;
      });
    }
    return sortableStudents;
  }, [students, sortConfig]);

  const requestSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add Student (POST)
  const handleAddSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_URL, formData);
      toast.success('Student added successfully!');
      fetchStudents();
      setFormData({
        studentId: '', fullName: '', dob: '', gender: '',
        email: '', phone: '', program: '', major: ''
      });
    } catch (error) {
      toast.error('Error adding student!');
    }
  };

  // Update Student (PUT) - includes admin header
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/${formData.studentId}`, formData, {
        headers: { 'x-role': 'admin' }
      });
      toast.success('Student updated successfully!');
      fetchStudents();
      setFormData({
        studentId: '', fullName: '', dob: '', gender: '',
        email: '', phone: '', program: '', major: ''
      });
      setIsEditing(false);
    } catch (error) {
      toast.error('Error updating student!');
    }
  };

  // Delete Student (DELETE) - includes admin header
  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`, { headers: { 'x-role': 'admin' } });
      toast.success('Student deleted!');
      fetchStudents();
    } catch (error) {
      toast.error('Error deleting student!');
    }
  };

  // Populate form for editing
  const handleEdit = (student) => {
    setFormData(student);
    setIsEditing(true);
  };

  // Search Students (calls backend search endpoint)
  const handleSearch = async () => {
    if (searchQuery.trim() === '') {
      fetchStudents();
      return;
    }
    try {
      const encodedQuery = encodeURIComponent(searchQuery);
      const response = await axios.get(`${API_URL}/search?q=${encodedQuery}`);
      setStudents(response.data);
    } catch (error) {
      toast.error('Error searching students!');
    }
  };

  // CSV File Upload
  const handleFileChange = (e) => {
    setCsvFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!csvFile) {
      toast.error('Please select a CSV file.');
      return;
    }
    const formDataUpload = new FormData();
    formDataUpload.append('file', csvFile);
    try {
      await axios.post(`${API_URL}/upload`, formDataUpload, {
        headers: { 'Content-Type': 'multipart/form-data', 'x-role': 'admin' }
      });
      toast.success('CSV uploaded successfully!');
      fetchStudents();
      fetchStats();
    } catch (error) {
      toast.error('Error uploading CSV file.');
    }
  };

  // Toggle Admin Mode
  const toggleAdmin = () => {
    setIsAdmin(!isAdmin);
  };

  // Prepare data for the bar chart
  const chartData = {
    labels: Object.keys(stats),
    datasets: [
      {
        label: 'Students per Program',
        data: Object.values(stats),
        backgroundColor: 'rgba(54, 162, 235, 0.6)'
      }
    ]
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Student Management System</h1>
        <button className="btn btn-secondary" onClick={toggleAdmin}>
          {isAdmin ? 'Logout Admin' : 'Login as Admin'}
        </button>
      </div>

      {/* Search Bar */}
      <div className="mb-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search by Student ID or Full Name..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button className="btn btn-primary" onClick={handleSearch}>Search</button>
        </div>
      </div>

      {/* Form Section */}
      <div className="card mb-5 shadow">
        <div className="card-body">
          <h4 className="card-title mb-3">{isEditing ? 'Update Student' : 'Add New Student'}</h4>
          <form onSubmit={isEditing ? handleEditSubmit : handleAddSubmit}>
            <div className="row">
              <div className="col-md-4 mb-3">
                <label className="form-label">Student ID</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="studentId" 
                  placeholder="Enter Student ID" 
                  value={formData.studentId} 
                  onChange={handleChange} 
                  required 
                  disabled={isEditing}
                />
              </div>
              <div className="col-md-8 mb-3">
                <label className="form-label">Full Name</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="fullName" 
                  placeholder="Enter Full Name (e.g., John Michael Doe)" 
                  value={formData.fullName} 
                  onChange={handleChange} 
                  required 
                />
              </div>
            </div>
            <div className="row">
              <div className="col-md-3 mb-3">
                <label className="form-label">Date of Birth</label>
                <input 
                  type="date" 
                  className="form-control" 
                  name="dob" 
                  value={formData.dob} 
                  onChange={handleChange} 
                  required 
                />
              </div>
              <div className="col-md-3 mb-3">
                <label className="form-label">Gender</label>
                <select 
                  className="form-select" 
                  name="gender" 
                  value={formData.gender} 
                  onChange={handleChange} 
                  required
                >
                  <option value="">Select Gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="col-md-3 mb-3">
                <label className="form-label">Email</label>
                <input 
                  type="email" 
                  className="form-control" 
                  name="email" 
                  placeholder="Enter Email" 
                  value={formData.email} 
                  onChange={handleChange} 
                  required 
                />
              </div>
              <div className="col-md-3 mb-3">
                <label className="form-label">Phone</label>
                <input 
                  type="tel" 
                  className="form-control" 
                  name="phone" 
                  placeholder="Enter Phone Number" 
                  value={formData.phone} 
                  onChange={handleChange} 
                  required 
                />
              </div>
            </div>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label className="form-label">Program</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="program" 
                  placeholder="Enter Program" 
                  value={formData.program} 
                  onChange={handleChange} 
                  required 
                />
              </div>
              <div className="col-md-6 mb-3">
                <label className="form-label">Major</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="major" 
                  placeholder="Enter Major" 
                  value={formData.major} 
                  onChange={handleChange} 
                  required 
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary">
              {isEditing ? 'Update Student' : 'Add Student'}
            </button>
          </form>
        </div>
      </div>

      {/* Admin-Only Features */}
      {isAdmin && (
        <>
          {/* CSV Upload */}
          <div className="card mb-5 shadow">
            <div className="card-body">
              <h4 className="card-title mb-3">Upload CSV Data</h4>
              <div className="mb-3">
                <input type="file" className="form-control" accept=".csv" onChange={handleFileChange} />
              </div>
              <button className="btn btn-success" onClick={handleUpload}>Upload CSV</button>
            </div>
          </div>

          {/* Data Visualization */}
          <div className="card mb-5 shadow">
            <div className="card-body">
              <h4 className="card-title mb-3">Student Distribution by Program</h4>
              {Object.keys(stats).length === 0 ? (
                <p className="text-muted">No program data available</p>
              ) : (
                <div style={{ height: '300px' }}>
                  <Bar data={chartData} options={{ responsive: true, maintainAspectRatio: false }} />
                </div>
              )}
            </div>
          </div>
        </>
      )}

      {/* Student List with Sortable Columns */}
      <h2 className="mb-4">Student List</h2>
      <div className="table-responsive shadow">
        <table className="table table-striped table-hover">
          <thead className="table-dark">
            <tr>
              <th onClick={() => requestSort('studentId')} style={{ cursor: 'pointer' }}>
                Student ID {sortConfig.key === 'studentId' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('fullName')} style={{ cursor: 'pointer' }}>
                Full Name {sortConfig.key === 'fullName' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('dob')} style={{ cursor: 'pointer' }}>
                DOB {sortConfig.key === 'dob' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('gender')} style={{ cursor: 'pointer' }}>
                Gender {sortConfig.key === 'gender' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('email')} style={{ cursor: 'pointer' }}>
                Email {sortConfig.key === 'email' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('phone')} style={{ cursor: 'pointer' }}>
                Phone {sortConfig.key === 'phone' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('program')} style={{ cursor: 'pointer' }}>
                Program {sortConfig.key === 'program' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('major')} style={{ cursor: 'pointer' }}>
                Major {sortConfig.key === 'major' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              {isAdmin && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {sortedStudents.map((s) => (
              <tr key={s.studentId}>
                <td>{s.studentId}</td>
                <td>{formatFullName(s.fullName)}</td>
                <td>{s.dob}</td>
                <td>{s.gender}</td>
                <td>{s.email}</td>
                <td>{s.phone}</td>
                <td>{s.program}</td>
                <td>{s.major}</td>
                {isAdmin && (
                  <td>
                    <button className="btn btn-warning btn-sm me-2" onClick={() => handleEdit(s)}>Edit</button>
                    <button className="btn btn-danger btn-sm" onClick={() => handleDelete(s.studentId)}>Delete</button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <ToastContainer />
    </div>
  );
}

export default App;
