import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

Chart.register(...registerables);

const API_URL = 'http://localhost:5000/students';

function App() {
  const [formData, setFormData] = useState({
    studentId: '',
    lastName: '',
    firstName: '',
    middleName: '',
    dob: '',
    gender: '',
    email: '',
    phone: '',
    streetAddress: '',
    country: ''
  });
  const [students, setStudents] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [csvFile, setCsvFile] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [stats, setStats] = useState({});
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

  // Helper function to parse a stored fullName into separate name parts.
  const parseFullName = (fullName) => {
    if (!fullName) return { lastName: '', firstName: '', middleName: '' };
    if (fullName.includes(',')) {
      const parts = fullName.split(',');
      const lastName = parts[0].trim();
      const names = parts[1].trim().split(/\s+/);
      const firstName = names[0] || '';
      const middleName = names.slice(1).join(' ') || '';
      return { lastName, firstName, middleName };
    } else {
      const parts = fullName.trim().split(/\s+/);
      if (parts.length === 1) return { lastName: '', firstName: parts[0], middleName: '' };
      const firstName = parts[0];
      const lastName = parts[parts.length - 1];
      const middleName = parts.slice(1, parts.length - 1).join(' ');
      return { lastName, firstName, middleName };
    }
  };

  // Fetch all students
  const fetchStudents = async () => {
    try {
      const response = await axios.get(API_URL);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  // Fetch stats for visualization (grouped by country)
  const fetchStats = async () => {
    try {
      const response = await axios.get(`${API_URL}/stats`);
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  useEffect(() => {
    fetchStudents();
    if (isAdmin) fetchStats();
  }, [isAdmin]);

  // Sorting: compute sorted student list
  const sortedStudents = useMemo(() => {
    let sortableStudents = [...students];
    if (sortConfig.key !== null) {
      sortableStudents.sort((a, b) => {
        const aValue = a[sortConfig.key] ? a[sortConfig.key].toString().toLowerCase() : '';
        const bValue = b[sortConfig.key] ? b[sortConfig.key].toString().toLowerCase() : '';
        if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
        if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
        return 0;
      });
    }
    return sortableStudents;
  }, [students, sortConfig]);

  const requestSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  // Handle form input changes for all fields.
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Combine the separate name parts into one fullName string.
  const combineFullName = () => {
    const last = formData.lastName.trim();
    const first = formData.firstName.trim();
    const middle = formData.middleName.trim();
    return middle ? `${last}, ${first} ${middle}` : `${last}, ${first}`;
  };

  // Add Student (POST)
  const handleAddSubmit = async (e) => {
    e.preventDefault();
    const fullName = combineFullName();
    const payload = {
      studentId: formData.studentId,
      fullName,
      dob: formData.dob,
      gender: formData.gender,
      email: formData.email,
      phone: formData.phone,
      streetAddress: formData.streetAddress,
      country: formData.country
    };
    try {
      await axios.post(API_URL, payload);
      toast.success('Student added successfully!');
      fetchStudents();
      setFormData({
        studentId: '',
        lastName: '',
        firstName: '',
        middleName: '',
        dob: '',
        gender: '',
        email: '',
        phone: '',
        streetAddress: '',
        country: ''
      });
    } catch (error) {
      toast.error('Error adding student!');
    }
  };

  // Update Student (PUT) - includes admin header
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    const fullName = combineFullName();
    const payload = {
      fullName,
      dob: formData.dob,
      gender: formData.gender,
      email: formData.email,
      phone: formData.phone,
      streetAddress: formData.streetAddress,
      country: formData.country
    };
    try {
      await axios.put(`${API_URL}/${formData.studentId}`, payload, {
        headers: { 'x-role': 'admin' }
      });
      toast.success('Student updated successfully!');
      fetchStudents();
      setFormData({
        studentId: '',
        lastName: '',
        firstName: '',
        middleName: '',
        dob: '',
        gender: '',
        email: '',
        phone: '',
        streetAddress: '',
        country: ''
      });
      setIsEditing(false);
    } catch (error) {
      toast.error('Error updating student!');
    }
  };

  // Delete Student (DELETE) - includes admin header
  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`, { headers: { 'x-role': 'admin' } });
      toast.success('Student deleted!');
      fetchStudents();
    } catch (error) {
      toast.error('Error deleting student!');
    }
  };

  // Populate form for editing and parse fullName into separate parts.
  const handleEdit = (student) => {
    const { lastName, firstName, middleName } = parseFullName(student.fullName);
    setFormData({
      studentId: student.studentId,
      lastName,
      firstName,
      middleName,
      dob: student.dob,
      gender: student.gender,
      email: student.email,
      phone: student.phone,
      streetAddress: student.streetAddress,
      country: student.country
    });
    setIsEditing(true);
  };

  // Search Students (calls backend search endpoint)
  const handleSearch = async () => {
    if (searchQuery.trim() === '') {
      fetchStudents();
      return;
    }
    try {
      const encodedQuery = encodeURIComponent(searchQuery);
      const response = await axios.get(`${API_URL}/search?q=${encodedQuery}`);
      setStudents(response.data);
    } catch (error) {
      toast.error('Error searching students!');
    }
  };

  // CSV File Upload
  const handleFileChange = (e) => {
    setCsvFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!csvFile) {
      toast.error('Please select a CSV file.');
      return;
    }
    const formDataUpload = new FormData();
    formDataUpload.append('file', csvFile);
    try {
      await axios.post(`${API_URL}/upload`, formDataUpload, {
        headers: { 'Content-Type': 'multipart/form-data', 'x-role': 'admin' }
      });
      toast.success('CSV uploaded successfully!');
      fetchStudents();
      fetchStats();
    } catch (error) {
      toast.error('Error uploading CSV file.');
    }
  };

  // Toggle Admin Mode
  const toggleAdmin = () => {
    setIsAdmin(!isAdmin);
  };

  // Prepare data for the bar chart (grouped by country)
  const chartData = {
    labels: Object.keys(stats),
    datasets: [
      {
        label: 'Students per Country',
        data: Object.values(stats),
        backgroundColor: 'rgba(54, 162, 235, 0.6)'
      }
    ]
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Student Management System</h1>
        <button className="btn btn-secondary" onClick={toggleAdmin}>
          {isAdmin ? 'Logout Admin' : 'Login as Admin'}
        </button>
      </div>

      {/* Search Bar */}
      <div className="mb-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search by Student ID or Full Name..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button className="btn btn-primary" onClick={handleSearch}>Search</button>
        </div>
      </div>

      {/* Form Section */}
      <div className="card mb-5 shadow">
        <div className="card-body">
          <h4 className="card-title mb-3">{isEditing ? 'Update Student' : 'Add New Student'}</h4>
          <form onSubmit={isEditing ? handleEditSubmit : handleAddSubmit}>
            <div className="row">
              <div className="col-md-4 mb-3">
                <label className="form-label">Student ID</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="studentId" 
                  placeholder="Enter Student ID" 
                  value={formData.studentId} 
                  onChange={handleChange} 
                  required 
                  disabled={isEditing}
                />
              </div>
              {/* Separate Name Inputs */}
              <div className="col-md-4 mb-3">
                <label className="form-label">Last Name</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="lastName" 
                  placeholder="Enter Last Name" 
                  value={formData.lastName} 
                  onChange={handleChange} 
                  required 
                />
              </div>
              <div className="col-md-4 mb-3">
                <label className="form-label">First Name</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="firstName" 
                  placeholder="Enter First Name" 
                  value={formData.firstName} 
                  onChange={handleChange} 
                  required 
                />
              </div>
              <div className="col-md-4 mb-3">
                <label className="form-label">Middle Name</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="middleName" 
                  placeholder="Enter Middle Name (optional)" 
                  value={formData.middleName} 
                  onChange={handleChange} 
                />
              </div>
            </div>
            <div className="row">
              <div className="col-md-3 mb-3">
                <label className="form-label">Date of Birth</label>
                <input 
                  type="date" 
                  className="form-control" 
                  name="dob" 
                  value={formData.dob} 
                  onChange={handleChange} 
                  required 
                />
              </div>
              <div className="col-md-3 mb-3">
                <label className="form-label">Gender</label>
                <select 
                  className="form-select" 
                  name="gender" 
                  value={formData.gender} 
                  onChange={handleChange} 
                  required
                >
                  <option value="">Select Gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="col-md-3 mb-3">
                <label className="form-label">Email</label>
                <input 
                  type="email" 
                  className="form-control" 
                  name="email" 
                  placeholder="Enter Email" 
                  value={formData.email} 
                  onChange={handleChange} 
                  required 
                />
              </div>
              <div className="col-md-3 mb-3">
                <label className="form-label">Phone</label>
                <input 
                  type="tel" 
                  className="form-control" 
                  name="phone" 
                  placeholder="Enter Phone Number" 
                  value={formData.phone} 
                  onChange={handleChange} 
                  required 
                />
              </div>
            </div>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label className="form-label">Street Address</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="streetAddress" 
                  placeholder="Enter Street Address" 
                  value={formData.streetAddress} 
                  onChange={handleChange} 
                  required 
                />
              </div>
              <div className="col-md-6 mb-3">
                <label className="form-label">Country</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="country" 
                  placeholder="Enter Country" 
                  value={formData.country} 
                  onChange={handleChange} 
                  required 
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary">
              {isEditing ? 'Update Student' : 'Add Student'}
            </button>
          </form>
        </div>
      </div>

      {/* Admin-Only Features */}
      {isAdmin && (
        <>
          <div className="card mb-5 shadow">
            <div className="card-body">
              <h4 className="card-title mb-3">Upload CSV Data</h4>
              <div className="mb-3">
                <input type="file" className="form-control" accept=".csv" onChange={handleFileChange} />
              </div>
              <button className="btn btn-success" onClick={handleUpload}>Upload CSV</button>
            </div>
          </div>

          <div className="card mb-5 shadow">
            <div className="card-body">
              <h4 className="card-title mb-3">Student Distribution by Country</h4>
              {Object.keys(stats).length === 0 ? (
                <p className="text-muted">No country data available</p>
              ) : (
                <div style={{ height: '300px' }}>
                  <Bar data={chartData} options={{ responsive: true, maintainAspectRatio: false }} />
                </div>
              )}
            </div>
          </div>
        </>
      )}

      {/* Student List with Sortable Columns */}
      <h2 className="mb-4">Student List</h2>
      <div className="table-responsive shadow">
        <table className="table table-striped table-hover">
          <thead className="table-dark">
            <tr>
              <th onClick={() => requestSort('studentId')} style={{ cursor: 'pointer' }}>
                Student ID {sortConfig.key === 'studentId' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('fullName')} style={{ cursor: 'pointer' }}>
                Full Name {sortConfig.key === 'fullName' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('dob')} style={{ cursor: 'pointer' }}>
                DOB {sortConfig.key === 'dob' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('gender')} style={{ cursor: 'pointer' }}>
                Gender {sortConfig.key === 'gender' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('email')} style={{ cursor: 'pointer' }}>
                Email {sortConfig.key === 'email' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('phone')} style={{ cursor: 'pointer' }}>
                Phone {sortConfig.key === 'phone' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('streetAddress')} style={{ cursor: 'pointer' }}>
                Street Address {sortConfig.key === 'streetAddress' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              <th onClick={() => requestSort('country')} style={{ cursor: 'pointer' }}>
                Country {sortConfig.key === 'country' ? (sortConfig.direction === 'asc' ? '▲' : '▼') : ''}
              </th>
              {isAdmin && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {sortedStudents.map((s) => (
              <tr key={s.studentId}>
                <td>{s.studentId}</td>
                <td>{s.fullName}</td>
                <td>{s.dob}</td>
                <td>{s.gender}</td>
                <td>{s.email}</td>
                <td>{s.phone}</td>
                <td>{s.streetAddress}</td>
                <td>{s.country}</td>
                {isAdmin && (
                  <td>
                    <button className="btn btn-warning btn-sm me-2" onClick={() => handleEdit(s)}>Edit</button>
                    <button className="btn btn-danger btn-sm" onClick={() => handleDelete(s.studentId)}>Delete</button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <ToastContainer />
    </div>
  );
}

export default App;
